
public class Arithmatic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int a = 9, b = 2, c = 3 ;
	        System.out.println(" a+b*c = " + ( a+b*c));
	        System.out.println("c+a/b = " + (c+a/b));
	        System.out.println("a%b+c = " + (a%b+c));
	        System.out.println("a*b+c = " + (a*b+c));
	}

}
